import ItemIcon from '../../assets/imports/analysis-item.png'
import ItemIconSelect from '../../assets/imports/analysis-selected-item.png'
import React, { useEffect, useState } from 'react'

const AnalysisBreakdown = ({ setEditing, demo, weights, currents, updateCurrent, setSelected }) => {

    function changeSelection(item) {
        setEditing(true)

        let newSelection = { ...currents }
        newSelection[demo] = item
        setSelected(newSelection)

        // Update the currents state as well
        updateCurrent(demo, item)

        localStorage.setItem('race', newSelection['race'])
        localStorage.setItem('age', newSelection['age'])
        localStorage.setItem('sex', newSelection['sex'])
    }

    useEffect(() => {
        let locRace = localStorage.getItem('race')
        let locAge = localStorage.getItem('age')
        let locSex = localStorage.getItem('sex')

        setSelected({
            'race': locRace,
            'age': locAge,
            'sex': locSex
        })
    }, [])

    // Ensure that weights[demo] is defined and is an array before mapping over it
    const demoWeights = weights ? weights[demo] : null;

    if (!Array.isArray(demoWeights)) {
        // If demoWeights is not an array, log an error and display a fallback UI
        console.error(`${demo} weights is not an array:`, demoWeights);
        return <div>Error: Weights data is not properly structured.</div>;
    }

    return (
        <div className="analysis__info--breakdown">
            <div className="analysis__info--breakdown--title">
                <h5>{demo}</h5>
                <h5>A.I. Confidence</h5>
            </div>

            {currents && demoWeights && demoWeights.map((item, i) => (
                item[0] !== currents[demo]
                    ?
                    <div
                        onClick={() => changeSelection(item[0])}
                        className="analysis__info--breakdown--item"
                        key={i}>
                        <h5>
                            <img src={ItemIcon} alt="" />
                            {item[0]}
                        </h5>
                        <h5>
                            {item[1]}{' '}
                            %
                        </h5>
                    </div>
                    :
                    <div className="analysis__info--breakdown--item analysis__info--breakdown--item--selected" key={i}>
                        <h5>
                            <img src={ItemIconSelect} alt="" />
                            {item[0]}
                        </h5>
                        <h5>
                            {item[1]}{' '}
                            %
                        </h5>
                    </div>
            ))}
        </div>
    )
}

export default AnalysisBreakdown
